mapper.insert:
INSERT INTO addr(
	 uNo
	,aStatus
	,aName
	,Addr1
	,Addr2
	,Addr3
	,aPhone
	,aMail
	,aComment
)values(
	 #{uNo}
	,#{aStatus}
	,#{aName}
	,#{Addr1}
	,#{Addr2}
	,#{Addr3}
	,#{aPhone}
	,#{aMail}
	,#{aComment}
)ON DUPLICATE KEY UPDATE
	 aStatus = #{aStatus}
	,aName = #{aName}
	,Addr1 = #{Addr1}
	,Addr2 = #{Addr2}
	,Addr3 = #{Addr3}
	,aPhone = #{aPhone}
	,aMail = #{aMail}
	,aComment = #{aComment}

