mapper.select:
SELECT count(*)
  FROM cart
 WHERE uNo = #{uNo}
   AND pNo = #{pNo}

mapper.insert:
INSERT INTO
cart (
	 uNo
	,pNo
	,cCount
) VALUES (
	 #{uNo}
	,#{pNo}
	,#{cCount}
)

	SELECT MAX(cNo)
	  FROM cart

mapper.update:
UPDATE cart
   SET cCount = #{cCount}
 WHERE cNo = #{cNo}
