mapper.update:
UPDATE Notice
SET uNo = #{uNo}, nTitle = #{nTitle}, nContents = #{nContents}
WHERE nNo = #{nNo}

UPDATE Notice 
SET delYN = 'Y'
WHERE nNo = #{nNo}

UPDATE Qna 
SET delYN = 'Y'
WHERE qNo = #{qNo}

UPDATE Qna
SET qAnswer = #{qAnswer}, qAnswerDate = NOW()
WHERE qNo = #{qNo}

mapper.select:
SELECT count(*) as cnt FROM User 
WHERE uNo = #{uNo} 
AND uStatu = 'A'

