mapper.select:
SELECT uId
  FROM user
 WHERE uName = #{uName}
   AND uMail = #{uMail}

UPDATE user
   SET uPw = #{uPw}
 WHERE uMail = #{uMail} 
   AND uId = #{uId}
   AND uName = #{uName}

mapper.update:
UPDATE user
   SET uPw = #{uPw}
	  ,uName = #{uName}
	  ,uPhone = #{uPhone}
	  ,uBisno = #{uBisno}
	  ,uAdd1 = #{uAdd1}
	  ,uAdd2 = #{uAdd2}
	  ,uAdd3 = #{uAdd3}
	  ,uMail = #{uMail}
 WHERE uNo = #{uNo}

mapper.select:
SELECT * FROM user
where uStatus ='N'

order by uNo asc limit #{sNum},#{perPageNum}

SELECT * FROM user
where uStatus ='B'

order by uNo asc limit #{sNum},#{perPageNum}

SELECT * FROM user
where uAlertNum > 0

order by uAlertNum desc limit #{sNum},#{perPageNum}

SELECT * FROM user
where uStatus = 'N'
and uNo = #{uNo}

SELECT * FROM user
where uStatus = 'B'
and uNo = #{uNo}

SELECT * FROM user
where uAlertNum > 0
and uNo = #{uNo}

mapper.update:
update user
set uAlertNum = uAlertNum + 1
where uNo = #{uNo}

update user
set uStatus = 'N'
where uNo = #{uNo}

update user
set uStatus = 'Y'
where uNo = #{uNo}

update user
set uStatus = 'B'
where uNo = #{uNo}
